def get_header(curl):
    header = {'X-Requested-With': 'XMLHttpRequest'}
    for i in curl[5:-1].split('-H')[1:]:
        key = i.split(':', 1)[0].strip(" '")
        value = i.split(':', 1)[1].strip("' ")
        if key != 'Accept-Encoding':
            header[key] = value
    return header


